import React, {useState, useEffect} from "react";
import Axios from 'axios';

function SignUp(){
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [alerts, setAlerts] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
        .then(result => {
            if(result.data){
                this.props.history.push('/');
            }
        })
    });

    handleChange = (event) => {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        });
    }

    onSubmit = (e) => {
        e.preventDefault()
        // Reset the alerts on every call to the API
        setAlerts([]);

        Axios.post('http://localhost:8000/signup', {email: email, password: password, confirmPassword: confirmPassword})
        .then((res) => { 
            setAlerts(res.data.errors);
            // If sign up was successful, redirect to home
            if(alerts[0].msg === 'Succesfully created.'){
                this.props.history.push('/');
            }
        })
        .catch(err =>{
            console.log(err);
        });
    }

    const alertsList = alerts.map(elem => {return <p key={elem.msg}>{elem.msg}</p>})
    return (
        <div>              
            <h1>Sign Up</h1>
            <div>
                <input type="email" name="email" placeholder="Email" onChange={(e) => {setEmail(e.value)}} value={this.state.email}/><br/>
                <input type="password" name="password" placeholder="Password" onChange={this.handleChange} value={this.state.password}/><br/>
                <input type="password" name="confirmPassword" placeholder="Confirm password" onChange={this.handleChange} value={this.state.confirmPassword}/><br/>
                <button onClick={this.onSubmit}>Submit</button>
                {alertsList}
            </div>
            <a href="/">Home</a><br />
            <a href="/login">Log in</a>
        </div>
    );
}

export default SignUp;
