import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter} from 'react-router-dom';

function Navigation(props){ 
    const [genres, setGenres] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:8000/genres/getAll', {withCredentials: true})
        .then(res => {
            const requestedGenres = res.data;
            setGenres(requestedGenres);
        });
    });

    const genreList = genres.map(genre => {
        return(
            <button type={"button"} onClick={() => {props.history.push(`/books?genre=${genre.name}`); window.location.reload()}} key={genre.name}>{genre.name}</button>
        );
    });

    return(
        <div className="nav-bar">
            <button onClick={() => {props.history.push('/books'); window.location.reload()}}>All</button>
            {genreList}
        </div>
    );
}

export default withRouter(Navigation);
