import Axios from 'axios';

export default async function isAuthenticated() {
    let result;

    await Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
        .then(result => {
            // If user is logged in
            if(result.data){
                result = true;    
            }else{
                result = false;
            }
        })
    
    return result;
}
