import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter} from 'react-router-dom';
import {BeatLoader} from 'react-spinners';
import '../css/home.css';
import Navigation from './partial/navigation';
import FeaturedBook from './partial/featuredBook';
import Footer from './partial/footer';

function Home(props){
    const [featuredBooks, setFeaturedBooks] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        Axios.get('http://localhost:8000/books/getAllFeatured', {withCredentials: true})
        .then(res => {
            const requestedBooks = res.data;
            setFeaturedBooks(requestedBooks);
            setLoading(false);
        })
    });

    const featuredBookList = featuredBooks.map(book => {
        return(
            <FeaturedBook book={book}/>
        )
    });

    if(loading){
        return(
            <div className="loading-container">
                <BeatLoader loading/>
            </div>
        )
    }
    return (
        <div>        
            <Navigation />
            <h2 className="featured-title">Featured</h2>
            <div className="home-container">
                <div className="featured-container">        
                    {featuredBookList}
                </div>
                <div className="footer-container">
                    <Footer />
                </div>
            </div>
        </div>
    )
}

export default withRouter(Home);
