import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {BeatLoader} from 'react-spinners';
import '../css/basket.css';

export default function Basket(props){
    const [items, setItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [isBasketEmpty, setisBasketEmpty] = useState(null);
    const [loading, setLoading] = useState(true);
    const xIcon = 
        <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" class="bi bi-x" viewBox="1 0 16 16">
            <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708z"/>
        </svg>;

    useEffect(() => {
        Axios.get('http://localhost:8000/basket/getItems', {withCredentials: true})
        .then(res => {
            if(res.data === 'emptyBasket'){
                setisBasketEmpty(true);
            }else{
                setisBasketEmpty(false);
                const requestedItems = res.data;
                setItems(requestedItems.array);
                setTotalPrice(requestedItems.totalPrice);
            }
            setLoading(false);
        })
    }, []);

    const clearBasket = () => {
        Axios.get('http://localhost:8000/basket/clear', {withCredentials: true})
        .then(() => {
            setisBasketEmpty(true)
            window.location.reload();
        });
    }

    if(loading){
        return(
            <div className="loading-container">
                <BeatLoader loading/>
            </div>
        )
    }
    if(isBasketEmpty){
        return(
            <div className="basket-empty-container">
                <h1>Your basket is empty. Go do some shopping.</h1>
            </div>
        )
    }
    const itemList = items.map(book => {
        return(
            <div className="book-in-basket" key={book.item._id}>
                <button onClick={() => {props.history.push("/book/" + book.item._id)}}>
                    <img src={`/images/${book.item.image}`} alt=""/>
                </button>
                <div className="book-in-basket-info">
                    <h2>{book.item.title}</h2>
                    <p>${book.item.price} x {book.quantity}</p>
                    {xIcon}
                </div>
            </div>
        )
    });
    return (
        <div className="basket-container">
            <div className="basket-items-container">
                {itemList}
            </div>
            <div className="basket-info">
                <h2>Total: ${totalPrice}</h2>
                <button onClick={clearBasket}>Clear basket</button>
            </div>
        </div>
    )
}
