import React, {useState, useEffect} from "react";
import Axios from 'axios';
import { Redirect } from 'react-router-dom'

function SignUp(props){
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [alerts, setAlerts] = useState([]);
    const [loggedIn, setLoggedIn] = useState(null);

    useEffect(() => {
        Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
        .then(result => {
            // If user is logged in
            if(result.data){
                setLoggedIn(true);          
            }else{
                setLoggedIn(false);
            }
        })
    });

    const onSubmit = (e) => {
        e.preventDefault()
        // Reset the alerts on every call to the API
        setAlerts([]);

        Axios.post('http://localhost:8000/signup', {email: email, password: password, confirmPassword: confirmPassword})
        .then((res) => { 
            setAlerts(res.data.errors);
            // If sign up was successful, redirect to home
            if(alerts.length > 0 && alerts[0].msg === 'Succesfully created.'){
                props.history.push('/');
            }
        })
        .catch(err =>{
            console.log(err);
        });
    }

    const alertsList = alerts.map(elem => {return <p key={elem.msg}>{elem.msg}</p>})
    if(loggedIn){
        return(
            <Redirect
            to={{
                pathname: '/',
                state: { from: props.location }
            }}
            />
        )
    }
    else if(!loggedIn){
    return (    
        <div>              
            <h1>Sign Up</h1>
            <div>
                <input type="email" name="email" placeholder="Email" onChange={(e) => {setEmail(e.target.value)}} value={email}/><br/>
                <input type="password" name="password" placeholder="Password" onChange={(e) => {setPassword(e.target.value)}} value={password}/><br/>
                <input type="password" name="confirmPassword" placeholder="Confirm password" onChange={(e) => {setConfirmPassword(e.target.value)}} value={confirmPassword}/><br/>
                <button onClick={onSubmit}>Submit</button>
                {alertsList}
            </div>
            <a href="/">Home</a><br />
            <a href="/login">Log in</a>
        </div>
    );
    }
}

export default SignUp;
