import React, {useState, useEffect} from "react"
import Axios from 'axios';

export default function Basket(props){
    const [items, setItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [isBasketEmpty, setisBasketEmpty] = useState(null);

    useEffect(() => {
        Axios.get('http://localhost:8000/basket/getItems', {withCredentials: true})
        .then(res => {
            if(res.data === 'emptyCart'){
                setisBasketEmpty(true);
            }else{
                setisBasketEmpty(false);
                const requestedItems = res.data;
                setItems(requestedItems.array);
                setTotalPrice(requestedItems.totalPrice);
            }
        })
    }, []);

    const clearBasket = () => {
        Axios.get('http://localhost:8000/basket/clear', {withCredentials: true})
        .then(() => {
            setisBasketEmpty(true)
            window.location.reload();
        });
    }

    if(isBasketEmpty === null){
        return(
            <h1>Loading...</h1>
        )
    }
    if(isBasketEmpty){
        return(
            <div>
                <h1>Cart is empty. Go shopping.</h1>
            </div>
        )
    }
    const itemList = items.map(book => {
        return(
            <div key={book.item._id}>
                <h2>{book.item.title}</h2>
                <p>${book.item.price} x {book.quantity}</p>
                <hr />
            </div>
        )
    });
    return (
        <div>
            <h1>Books</h1>
            {itemList}
            Total: ${totalPrice}
            <button onClick={clearBasket}>Clear basket</button>
        </div>
    )
}
