import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter} from 'react-router-dom';
import {LogOutIcon, BasketIcon} from './icons';
import '../../css/header.css';

function Header(props){
    const [isLoggedIn, setIsLoggedIn] = useState(null);
    const [isAdmin, setIsAdmin] = useState(false);
    const [loading, setLoading] = useState(true);



    useEffect(() => {
        Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
        .then(result => {
            if(result.data){
                setIsLoggedIn(result.data)
                Axios.get('http://localhost:8000/checkAdminAuthentication', { withCredentials: true })
                .then(data => {
                    setIsAdmin(data.data);
                    setLoading(false);
                });
            }else{
                setIsAdmin(false);
                setIsLoggedIn(false);
                setLoading(false);
            }
        });
    }, []);

    const logOut = () => {
        Axios.get('http://localhost:8000/logout', { withCredentials: true })
        .then(result => {
            props.history.push(result.data);
            setIsLoggedIn(false);
        })
    }

    if(loading){
        return(
            <div className="header">
                <div className="logo" onClick={() => {props.history.push('/')}}><p>Alexandria</p></div>       
            </div>
        )
    }

    if(isLoggedIn){
        return(
            <div className="header">
                <div className="logo" onClick={() => {props.history.push('/')}}><p>Alexandria</p></div> 
                <div className="log">
                    <button className="basket-button" title="Basket" onClick={() => {props.history.push('/basket')}}>
                        <BasketIcon width="20" height="20" />
                    </button>             
                    <button className="log-out-button" title="Log Out" onClick={logOut}>
                        <LogOutIcon width="20" height="20" />
                    </button>
                    <button>Admin panel</button>
                </div>       
            </div>
        );
    }

    return(
        <div className="header">
            <div className="logo" onClick={() => {props.history.push('/')}}><p>Alexandria</p></div>
            <div className="log">
                <button className="basket-button" title="Basket" onClick={() => {props.history.push('/basket')}}>
                    <BasketIcon width="20" height="20" />
                </button>
                <button onClick={() => {props.history.push('/login')}}>Login</button>
            </div>
        </div>
    );
}

export default withRouter(Header);
