import React, {useState, useEffect} from "react"
import Axios from 'axios';
import { withRouter } from 'react-router-dom';

import "../css/header.css"

function Header(props){
    const [isLoggedIn, setIsLoggedIn] = useState(null);

    useEffect(() => {
      Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
      .then(result => {
          // If user is logged in
          setIsLoggedIn(result.data);
      })
    }, []);

    const logOut = () => {
        Axios.get('http://localhost:8000/logout', { withCredentials: true })
        .then(result => {
            props.history.push(result.data);
            setIsLoggedIn(false);
        })
    }
    
    return(
        <div className="header">
            <div className="logo"><h1>Alexandria</h1></div>
            <div className="logOut"><button style={{display: isLoggedIn ? '' : 'none'}} onClick={logOut}>Log out</button></div>
        </div>
    );
}

export default withRouter(Header);
