import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter} from 'react-router-dom';
import {BeatLoader} from 'react-spinners';
import {toast} from 'react-toastify';
import '../css/books.css';
import 'react-toastify/dist/ReactToastify.css';

function Book(props){
    const [book, setBook] = useState({});
    const {id} = props.match.params;
    const [loading, setLoading] = useState(true);
    const checkmarkIcom = 
    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" class="bi bi-check" viewBox="0 -3 16 16">
        <path fill-rule="evenodd" d="M10.97 4.97a.75.75 0 0 1 1.071 1.05l-3.992 4.99a.75.75 0 0 1-1.08.02L4.324 8.384a.75.75 0 1 1 1.06-1.06l2.094 2.093 3.473-4.425a.236.236 0 0 1 .02-.022z"/>
    </svg>;

    useEffect(() => {
        toast.configure();
        Axios.get(`http://localhost:8000/books/get/${id}`, {withCredentials: true})
        .then(res => {
            setBook(res.data); 
            setLoading(false);
        })
    });

    const addToBasket = (id) => {
        Axios.get(`http://localhost:8000/basket/add/${id}`, {withCredentials: true})
        .then(() => {toast.info("Book added to basket!", {position: toast.POSITION.BOTTOM_RIGHT, pauseOnHover: false, autoClose: 2000})});
    };

    const availability = book.available ? 
    <p className="single-book-in-stock">
        {checkmarkIcom}
        In stock
    </p> 
    : <p className="single-book-out-of-stock">Out of stock</p>;

    if(loading){
        return(
            <div className="loading-container">
                <BeatLoader loading/>
            </div>
        )
    }
    return (
        <div className="single-book-container">
            <div className="single-book-image">
                <img src={`/images/${book.image}`} alt=""/>
            </div>
            <div className="single-book-info">
                <h1 className="single-book-title">{book.title}</h1>
                <h2 className="single-book-author">by {book.author}</h2>
                <p className="single-book-genre">Genre: {book.genre}</p>
                <p className="single-book-date">Published: {book.publishedDate}</p>
                <p className="single-book-price">${book.price}</p>
                {availability}
                <button className="single-book-add-to-basket" onClick={() => {addToBasket(book._id)}}
                style={{display: book.available ? '' : 'none'}}>
                    Add to basket
                </button>
                <div className="single-book-description">
                    <h1 style={{display: book.description === undefined ? 'none' : ''}}>Description</h1> 
                    {book.description}
                </div>
            </div> 
        </div>
    )
}

export default withRouter(Book);
