import React, {useState, useEffect} from 'react';
import {withRouter} from 'react-router-dom';
import {app} from '../../firebase/index';
import {BeatLoader} from 'react-spinners';

function FeaturedBook(props){
    const [imageUrl, setImageUrl] = useState('');
    const [Loading, setLoading] = useState(true);

    useEffect(() => {
        const storageRef = app.storage().ref();
        storageRef.child(`${props.book.image}`).getDownloadURL().then(
            (url) => {
                setImageUrl(url);
                setLoading(false);
            }
        )
        .catch(error => {
            return <h1>Something went wrong.</h1>
        })
    });

    if(Loading){
        return(
            <div className="featured-book-item">
                <div className="featured-image-container">
                    <BeatLoader loading/>
                </div>
            </div>
        );
    }

    return(
        <div className="featured-book-item">
                <div className="featured-image-container">
                    <button onClick={() => {props.history.push("/book/" + props.book._id)}}>
                        <img src={imageUrl} alt=""/>
                    </button>
                </div>
            </div>
    );
}

export default withRouter(FeaturedBook);
