import React, {useState, useEffect} from "react"
import Axios from 'axios';
import { withRouter } from 'react-router-dom';

function Book(props){
    const [books, setBooks] = useState([]);
    const [id] = useState(props.id);

    useEffect(() => {
        Axios.get(`http://localhost:8000/books/get/${id}`, {withCredentials: true})
        .then(res => {
            const requestedBooks = res.data;
            setBooks(requestedBooks);
        })
    }, []);

    const addToBasket = (id) => {
        Axios.get(`http://localhost:8000/basket/add/${id}`, {withCredentials: true})
        .then(() => {
            window.location.reload();
        });
    };

    const bookList = books.map(book => {
        return(
            <div className="book-item" key={book._id}>
                <div className="image-container">
                    <button>
                        <img src={`/images/${book.image}`} alt=""/>
                    </button>
                </div>
                <div className="description-container">
                    <h2 className="book-title">{book.title}</h2>
                    <h3 className="book-author">{book.author}</h3>
                    <button onClick={() => {addToBasket(book._id)}}>Add to basket</button>
                </div>
            </div>
        )
    });
    return (
        <div className="book-container">
            {bookList}
        </div>
    )
}

export default withRouter(Book);
