import React, {useState, useEffect} from "react"
import Axios from 'axios';

export default function Cart(props){
    const [items, setItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);

    useEffect(() => {
        Axios.get('http://localhost:8000/cart/getItems', {withCredentials: true})
        .then(res => {
            const requestedItems = res.data;
            setItems(requestedItems.array);
            setTotalPrice(requestedItems.totalPrice);
        })
    }, []);

    const itemList = items.map(book => {
        return(
            <div key={book.item._id}>
                <h2>{book.item.title}</h2>
                <p>${book.item.price} x {book.quantity}</p>
                <hr />
            </div>
        )
    });
    return (
        <div>
            <h1>Books</h1>
            {itemList}
            Total: ${totalPrice}
        </div>
    )
}
