import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter} from 'react-router-dom';
import {BeatLoader} from 'react-spinners';
import {toast} from 'react-toastify';
import '../css/books.css';
import 'react-toastify/dist/ReactToastify.css';
import Navigation from './navigation';
import {AddToBasketIcon, DescendingIcon, AscendingIcon} from './icons';
const qs = require('qs');

function Books(props){
    const [books, setBooks] = useState([]);
    const [genre] = useState(qs.parse(props.location.search, {ignoreQueryPrefix: true}).genre);
    const [sortBy] = useState(qs.parse(props.location.search, {ignoreQueryPrefix: true}).orderby);
    const [descending] = useState(qs.parse(props.location.search, {ignoreQueryPrefix: true}).descending);
    const [loading, setLoading] = useState(true);
    toast.configure();

    useEffect(() => {
        const APIRequest = `http://localhost:8000/books/getAll?genre=${genre ? genre : ''}&orderby=${sortBy ? sortBy : 'title'}&descending=${descending ? descending : 'false'}`;
        Axios.get(APIRequest, {withCredentials: true})
        .then(res => {
            const requestedBooks = res.data;
            setBooks(requestedBooks);
            setLoading(false); 
        })
    });

    const addToBasket = (id) => {
        Axios.get(`http://localhost:8000/basket/add/${id}`, {withCredentials: true})
        .then(() => {toast.info("Book added to basket!", {position: toast.POSITION.BOTTOM_RIGHT, pauseOnHover: false, autoClose: 2000})});
    };

    const redirect = (genre, orderBy, descending) => {
        props.history.push({
            pathname: '/books',
            search: `?genre=${genre ? genre : ''}&orderby=${orderBy ? orderBy : 'title'}&descending=${descending ? descending : 'true'}`
          })
          window.location.reload();
    }

    const bookList = books.map(book => {
        return(
            <div className="book-item" key={book._id}>
                <div className="image-container">
                    <button onClick={() => {props.history.push("/book/" + book._id)}}>
                        <img src={`/images/${book.image}`} alt=""/>
                    </button>
                </div>
                <div className="description-container">
                    <h2 className="book-title">{book.title}</h2>
                    <h3 className="book-author">{book.author}</h3>
                    <button className="book-add-to-basket" title="Add to basket" onClick={() => {addToBasket(book._id)}} style={{display: book.available ? '' : 'none'}}>
                        <AddToBasketIcon width="20" height="20"/>
                    </button>
                </div>
            </div>
        )
    });

    if(loading){
        return(
            <div className="loading-container">
                <BeatLoader loading/>
            </div>
        )
    }
    return (
        <div>
            <Navigation />
            <h1 className="genre-title">{genre}</h1>
            <div className="sort-options-container">
                <form className="order-by-form">
                    <label>
                        Order by:&nbsp;
                        <select value={sortBy} onChange={(e) => redirect(genre, e.target.value, descending)}>
                            <option value="title">Title</option>
                            <option value="author">Author</option>
                            <option value="price">Price</option>
                        </select>
                    </label>  
                </form>
                <button onClick={() => redirect(genre, sortBy, descending === 'true' ? 'false' : 'true')}>
                    {descending === 'true' ? <DescendingIcon width="20" height="20"/> 
                    : <AscendingIcon width="20" height="20"/>}
                </button>
            </div>
            <div className="book-container">
                {bookList}
            </div>
        </div>
    )
}

export default withRouter(Books);
