import React, {useState, useEffect} from "react"
import Axios from 'axios';
import { withRouter } from 'react-router-dom';

import "../css/header.css"

function Header(props){
    const [isLoggedIn, setIsLoggedIn] = useState(null);

    useEffect(() => {
      Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
      .then(result => {
          // If user is logged in
          setIsLoggedIn(result.data);
      })
    }, []);

    const logOut = () => {
        Axios.get('http://localhost:8000/logout', { withCredentials: true })
        .then(result => {
            props.history.push(result.data);
            setIsLoggedIn(false);
        })
    }
    if(isLoggedIn === null){
        return(
            <h3>wait</h3>
        )
    }
    if(isLoggedIn){
        return(
            <div className="header">
                <div className="logo" onClick={() => {props.history.push('/')}}><p>Alexandria</p></div>
                <div className="log">
                    <button onClick={logOut}>Log Out</button>
                </div>
            </div>
        );
    }

    return(
        <div className="header">
            <div className="logo" onClick={() => {props.history.push('/')}}><p>Alexandria</p></div>
            <div className="log">
                <button onClick={() => {props.history.push('/')}}>Log In</button>
            </div>
        </div>
    );
}

export default withRouter(Header);
