import React, {useState, useEffect} from "react";
import Axios from 'axios';

function LogIn(){
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [alerts, setAlerts] = useState([]);

    const onSubmit = (e) => {
        e.preventDefault()
        // Reset the alerts on every call to the API
        setAlerts([]);

        Axios.post('http://localhost:8000/login', {email: email, password: password}, { withCredentials: true })
        .then((res) => {
            setAlerts(res.data.errors);
        })
        .catch(err =>{
            console.log(err);
        });
    }


    const alertsList = alerts.map(elem => {return <p key={elem.msg}>{elem.msg}</p>})
    return (
        <div>
            <h1>Log in</h1>
            <div>
                <input type="email" name="email" placeholder="Email" onChange={(e) => {setEmail(e.target.value)}} value={email}/><br/>
                <input type="password" name="password" placeholder="Password" onChange={(e) => {setPassword(e.target.value)}} value={password}/><br/>
                <button onClick={this.onSubmit}>Submit</button>
            </div>
            {alertsList}
            <a href="/">Home</a><br />
            <a href="/signup">Sign up</a>
        </div>
    );
}

export default LogIn;
