import React, {Component} from "react";
import Axios from 'axios';

class SignUp extends Component{
    constructor(){
        super();
        this.state = {
            email: '',
            password: '',
            alerts: []
        };        
    }

    handleChange = (event) => {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        });
    }

    onSubmit = () => {
        Axios.post('http://localhost:8000/api/signup', {email: this.state.email, password: this.state.password})
        .then((res) => {
            this.setState({alerts: res.data});
        })
        .catch(err =>{
            console.log(err.response.data.errors);
            if(err.response.data.errors !== 'alreadyLoggedIn'){
                this.props.history.push('/signup');
            }
          });
    }

    render(){
        const alertElements = this.state.alerts.map(alert => {
            return <p key={alert.value}>{alert.msg}</p>
        });
        return (
            <div>              
                <h1>Sign Up</h1>
                <div>
                    <input type="text" name="email" placeholder="Email" onChange={this.handleChange} value={this.state.email}/><br/>
                    <input type="text" name="password" placeholder="Password" onChange={this.handleChange} value={this.state.password}/><br/>
                    {alertElements}
                    <button onClick={this.onSubmit}>Submit</button>
                </div>
                <a href="/">Home</a><br />
                <a href="/login">Log in</a>
            </div>
        );
    }
}

export default SignUp;
