import React, {Component} from "react";
import Axios from 'axios';

class LogIn extends Component{
    constructor(){
        super();
        this.state = {
            email: '',
            password: '',
            alerts: []
        };        
    }

    handleChange = (event) => {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        });
    }

    onSubmit = (e) => {
        e.preventDefault()
        // Reset the alerts on every call to the API
        this.setState({alerts: []});    
        const {email, password} = this.state;

        Axios.post('http://localhost:8000/login', {email: email, password: password})
        .then((res) => {
            this.setState({alerts: res.data});
        })
        .catch(err =>{
            console.log(err);
        });
    }

    render(){
        return (
            <div>
                <h1>Log in</h1>
                <div>
                    <input type="email" name="email" placeholder="Email" onChange={this.handleChange} value={this.state.email}/><br/>
                    <input type="password" name="password" placeholder="Password" onChange={this.handleChange} value={this.state.password}/><br/>
                    <button onClick={this.onSubmit}>Submit</button>
                    {this.state.alerts}
                </div>
                <a href="/">Home</a><br />
                <a href="/signup">Sign up</a>
            </div>
        );
    }
}

export default LogIn;
