import React, {useState, useEffect} from "react"
import Axios from 'axios';
import { withRouter } from 'react-router-dom';

import "../css/header.css"

function Header(props){
    const [isLoggedIn, setIsLoggedIn] = useState(null);
    const [itemsInBasketQuantity, setItemsInBasketQuantity] = useState(0);

    useEffect(() => {
      Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
      .then(result => {
          // If user is logged in
          setIsLoggedIn(result.data);
      });

      Axios.get('http://localhost:8000/basket/getQuantity', { withCredentials: true })
      .then(result => {
        setItemsInBasketQuantity(result.data);
      });
    }, []);

    const logOut = () => {
        Axios.get('http://localhost:8000/logout', { withCredentials: true })
        .then(result => {
            props.history.push(result.data);
            setIsLoggedIn(false);
        })
    }

    if(isLoggedIn){
        return(
            <div className="header">
                <div className="logo" onClick={() => {props.history.push('/')}}><p>Alexandria</p></div>
                <div className="log">
                    <button className="basket-button" onClick={() => {props.history.push('/basket')}}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" class="bi bi-basket3" viewBox="0 0 16 16">
                        <path fill-rule="evenodd" d="M5.757 1.071a.5.5 0 0 1 .172.686L3.383 6h9.234L10.07 1.757a.5.5 0 1 1 .858-.514L13.783 6H15.5a.5.5 0 0 1 .5.5v1a.5.5 0 0 1-.5.5H.5a.5.5 0 0 1-.5-.5v-1A.5.5 0 0 1 .5 6h1.717L5.07 1.243a.5.5 0 0 1 .686-.172zM3.394 15l-1.48-6h-.97l1.525 6.426a.75.75 0 0 0 .729.574h9.606a.75.75 0 0 0 .73-.574L15.056 9h-.972l-1.479 6h-9.21z"/>
                    </svg>
                    </button>             
                    <button className="log-out-button" onClick={logOut}>
                        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" class="bi bi-box-arrow-right" viewBox="0 0 16 16">
                            <path fill-rule="evenodd" d="M10 12.5a.5.5 0 0 1-.5.5h-8a.5.5 0 0 1-.5-.5v-9a.5.5 0 0 1 .5-.5h8a.5.5 0 0 1 .5.5v2a.5.5 0 0 0 1 0v-2A1.5 1.5 0 0 0 9.5 2h-8A1.5 1.5 0 0 0 0 3.5v9A1.5 1.5 0 0 0 1.5 14h8a1.5 1.5 0 0 0 1.5-1.5v-2a.5.5 0 0 0-1 0v2z"/>
                            <path fill-rule="evenodd" d="M15.854 8.354a.5.5 0 0 0 0-.708l-3-3a.5.5 0 0 0-.708.708L14.293 7.5H5.5a.5.5 0 0 0 0 1h8.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3z"/>
                        </svg>
                    </button>
                </div>       
            </div>
        );
    }

    return(
        <div className="header">
            <div className="logo" onClick={() => {props.history.push('/')}}><p>Alexandria</p></div>
            <div className="log">
                <button onClick={() => {props.history.push('/basket')}}>Basket({itemsInBasketQuantity})</button>
                <button onClick={() => {props.history.push('/login')}}>Login</button>
            </div>
        </div>
    );
}

export default withRouter(Header);
