import React, {useState, useEffect} from "react"
import Axios from 'axios';

export default function Books(props){
    const [books, setBooks] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:8000/books', {withCredentials: true})
        .then(res => {
            const requestedBooks = res.data;
            setBooks(requestedBooks);
        })
    }, []);

    const addToBasket = (id) => {
        Axios.get(`http://localhost:8000/basket/add/${id}`, {withCredentials: true})
        .then(() => {
            window.location.reload();
        });
    };

    const bookList = books.map(book => {
        return(
            <div key={book._id}>
                <img src="./src/images/sun_and_steel.jpg" alt=""/>
                <h2>{book.title}</h2>
                <h3>{book.author} - {book.publishedDate}</h3>
                <p>{book.genre}</p>
                <p>${book.price}</p>
                <button onClick={() => {addToBasket(book._id)}}>Add to basket</button>
                <hr />
            </div>
        )
    });
    return (
        <div>
                <h1>Books</h1>
                {bookList}
        </div>
    )
}
