import React, {useState, useEffect} from 'react';
import {withRouter} from 'react-router-dom';
import {app} from '../../firebase/index';
import {BeatLoader} from 'react-spinners';

function FeaturedBook(props){
    const [imageUrl, setImageUrl] = useState('');
    const [Loading, setLoading] = useState(true);

    useEffect(() => {
        const storageRef = app.storage().ref();
        if (storageRef.child(props.book.image) !== null) {
            storageRef.child(`${props.book.image}`).getDownloadURL().then(
                (url) => {
                    setImageUrl(url);
                    setLoading(false);
                }
            );
        }else{
            storageRef.child('1984.jpg').getDownloadURL().then(
                (url) => {
                    setImageUrl(url);
                    setLoading(false);
                }
            );
        }
    });

    if(Loading){
        return(
            <div className="featured-book-item">
                <div className="featured-image-container">
                    <BeatLoader loading/>
                </div>
            </div>
        );
    }

    return(
        <div className="featured-book-item">
                <div className="featured-image-container">
                    <button onClick={() => {props.history.push("/book/" + props.book._id)}}>
                        <img src={imageUrl} alt=""/>
                    </button>
                </div>
            </div>
    );
}

export default withRouter(FeaturedBook);
