import React, {useState, useEffect} from "react";
import Axios from 'axios';
import { Redirect } from 'react-router-dom'

function SignUp(props){
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [alerts, setAlerts] = useState([]);
    const [isLoggedIn, setIsLoggedIn] = useState(null);

    useEffect(() => {
        Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
        .then(result => {
            setIsLoggedIn(result.data);
        })
    }, []);

    const onSubmit = (e) => {
        e.preventDefault()
        // Reset the alerts on every call to the API
        setAlerts([]);

        Axios.post('http://localhost:8000/signup', {email: email, password: password, confirmPassword: confirmPassword})
        .then((res) => { 
            const errors = res.data.errors;
            // If sign up was successful, redirect to home
            if(errors.length > 0 && errors[0].msg === 'Succesfully created'){
                props.history.push('/');
            }else{
                setAlerts(errors);
            }
        })
        .catch(err =>{
            console.log(err);
        });
    }
   
    if(isLoggedIn === null){
        return(
            <h1>Loading...</h1>
        )
    }
    if(isLoggedIn){
        return(
            <Redirect
            to={{
                pathname: '/',
                state: { from: props.location }
            }}
            />
        )
    }
    const alertsList = alerts.map(elem => {return <p key={elem.msg}>{elem.msg}</p>})
    return (    
        <div className="form-container">
            <form className="form"> 
                <p className="form-title">Register as a new user</p>
                <div className="input-title">Email</div>
                <input type="email" name="email" onChange={(e) => {setEmail(e.target.value)}} value={email}/><br/>
                <div className="input-title">Passoword</div>
                <input type="password" name="password" onChange={(e) => {setPassword(e.target.value)}} value={password}/><br/>
                <div className="input-title">Confirm password</div>
                <input type="password" name="confirmPassword" onChange={(e) => {setConfirmPassword(e.target.value)}} value={confirmPassword}/><br/>
                <div className="submit-container">
                    <button className="submit-button" onClick={onSubmit}>SIGN UP</button>
                </div>
                {alertsList}
            </form>
        </div>
    );
}

export default SignUp;
