import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter} from 'react-router-dom';

function Navigation(props){ 
    const [genres, setGenres] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:8000/genres/getAll', {withCredentials: true})
        .then(res => {
            const requestedGenres = res.data;
            setGenres(requestedGenres);
        });
    });

    return(
        <div className="nav-bar">
            <button onClick={() => {props.history.push('/books'); window.location.reload()}}>All</button>
            <button onClick={() => {props.history.push('/books?genre=Novel'); window.location.reload()}}>Novels</button>
            <button onClick={() => {props.history.push('/books?genre=Autobiographical'); window.location.reload()}}>Autobiographical</button>
            <button onClick={() => {props.history.push('/books?genre=Sci-Fi'); window.location.reload()}}>Sci-Fi</button>
        </div>
    );
}

export default withRouter(Navigation);
