import React, {useState} from 'react';
import {app} from '../../firebase/index';
import Axios from 'axios';

function Add(){
    const [image, setImage] = useState(null);
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [genre, setGenre] = useState('');
    const [price, setPrice] = useState(0);
    const [publishedDate, setPublishedDate] = useState('');
    const [description, setDescription] = useState('');
    const [availability, setAvailability] = useState(true);
    const [fetured, setFetured] = useState(false);

    const handleChange = event => {
        const img = event.target.files[0];
        setImage(img);
    }

    const uploadImage = () => {
        const storageRef = app.storage().ref();
        const fileRef = storageRef.child(image.name);
        fileRef.put(image).then(() => {
            console.log('File uploaded');
            window.location.reload();
        });
    }

    const handleSubmit = () => {
        uploadImage();
    }

    return(
        <div>
            <h1>Add new book</h1>
            <input type="text" name="title" placeholder="Title" onChange={(e) => setTitle(e.target.value)}/><br />
            <input type="text" name="author" placeholder="Author" onChange={(e) => setAuthor(e.target.value)}/><br />
            <input type="text" name="genre" placeholder="Genre" onChange={(e) => setGenre(e.target.value)}/><br />
            <input type="number" name="price" min="1" max="999" placeholder="Price" onChange={(e) => setPrice(e.target.value)}/><br />
            <input type="text" name="publishedDate" placeholder="Published Date" onChange={(e) => setPublishedDate(e.target.value)}/><br />
            Is available<input type="checkbox" name="availability" onChange={() => setAvailability(!availability)} /><br />
            <input type="file" onChange={(e) => handleChange(e)}/><br />
            <button onClick={handleSubmit} style={{display: image ? '' : 'none'}}>Upload</button>
        </div>
    );
}

export default Add;
