import Axios from 'axios';

export default function isAuthenticated() {
    Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
        .then(result => {
            // If user is logged in
            if(result.data){
                return true;    
            }else{
                return false;
            }
        })
}
