import React, {useState, useEffect} from "react"
import Axios from 'axios';

import Header from "./header"

export default function Cart(props){
    const [items, setItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [isCartEmpty, setisCartEmpty] = useState(null);

    useEffect(() => {
        Axios.get('http://localhost:8000/cart/getItems', {withCredentials: true})
        .then(res => {
            if(res.data === 'emptyCart'){
                setisCartEmpty(true);
            }else{
                setisCartEmpty(false);
                const requestedItems = res.data;
                setItems(requestedItems.array);
                setTotalPrice(requestedItems.totalPrice);
            }
        })
    }, []);

    const clearCart = () => {
        Axios.get('http://localhost:8000/cart/clear', {withCredentials: true})
        .then(setisCartEmpty(true));
    }

    if(isCartEmpty === null){
        return(
            <h1>Loading...</h1>
        )
    }
    if(isCartEmpty){
        return(
            <div>
                <h1>Cart is empty. Go shopping.</h1>
            </div>
        )
    }
    const itemList = items.map(book => {
        return(
            <div key={book.item._id}>
                <h2>{book.item.title}</h2>
                <p>${book.item.price} x {book.quantity}</p>
                <hr />
            </div>
        )
    });
    return (
        <div>
            <h1>Books</h1>
            {itemList}
            Total: ${totalPrice}
            <button onClick={clearCart}>Clear cart</button>
        </div>
    )
}
