import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter} from 'react-router-dom';
import {BeatLoader} from 'react-spinners';
import {toast} from 'react-toastify';
import '../css/books.css';
import 'react-toastify/dist/ReactToastify.css';
import Navigation from './navigation';
const qs = require('qs');

function Books(props){
    const [books, setBooks] = useState([]);
    const [genre] = useState(props.match.params.genre);
    const [sortBy] = useState(qs.parse(props.location.search, {ignoreQueryPrefix: true}).orderby);
    const [descending] = useState(qs.parse(props.location.search, {ignoreQueryPrefix: true}).descending);
    const [loading, setLoading] = useState(true);
    const addToBasketIcon =
        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" class="bi bi-bag-plus" viewBox="0 0 16 16">
            <path fill-rule="evenodd" d="M8 1a2.5 2.5 0 0 0-2.5 2.5V4h5v-.5A2.5 2.5 0 0 0 8 1zm3.5 3v-.5a3.5 3.5 0 1 0-7 0V4H1v10a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V4h-3.5zM2 5v9a1 1 0 0 0 1 1h10a1 1 0 0 0 1-1V5H2z"/>
            <path fill-rule="evenodd" d="M8 7.5a.5.5 0 0 1 .5.5v1.5H10a.5.5 0 0 1 0 1H8.5V12a.5.5 0 0 1-1 0v-1.5H6a.5.5 0 0 1 0-1h1.5V8a.5.5 0 0 1 .5-.5z"/>
        </svg>;

    useEffect(() => {
        toast.configure();
        let APIRequest = `http://localhost:8000/books/getAll?orderby=${sortBy}`;
        if(genre !== undefined){
            APIRequest = `http://localhost:8000/books/getAll/${genre}?orderby=${sortBy}`;
        }  
        Axios.get(APIRequest, {withCredentials: true})
        .then(res => {
            const requestedBooks = res.data;
            setBooks(requestedBooks);
            setLoading(false);
        })
    });

    const addToBasket = (id) => {
        Axios.get(`http://localhost:8000/basket/add/${id}`, {withCredentials: true})
        .then(() => {toast.info("Book added to basket!", {position: toast.POSITION.BOTTOM_RIGHT, pauseOnHover: false, autoClose: 2000})});
    };

    const bookList = books.map(book => {
        return(
            <div className="book-item" key={book._id}>
                <div className="image-container">
                    <button onClick={() => {props.history.push("/book/" + book._id)}}>
                        <img src={`/images/${book.image}`} alt=""/>
                    </button>
                </div>
                <div className="description-container">
                    <h2 className="book-title">{book.title}</h2>
                    <h3 className="book-author">{book.author}</h3>
                    <button className="book-add-to-basket" title="Add to basket" onClick={() => {addToBasket(book._id)}} style={{display: book.available ? '' : 'none'}}>
                        {addToBasketIcon}
                    </button>
                </div>
            </div>
        )
    });

    if(loading){
        return(
            <div className="loading-container">
                <BeatLoader loading/>
            </div>
        )
    }
    return (
        <div>
            <Navigation />
            <h1 className="genre-title">{genre}</h1>
            <form className="order-by-form">
                <label>
                    Order by:&nbsp;
                    <select value={sortBy} onChange={(e) => {
                        props.history.push({
                            pathname: genre !== undefined ? `/books/${genre}` : '/books/',
                            search: `?orderby=${e.target.value}`
                          })
                          window.location.reload();
                    }}>
                        <option value="title">Title</option>
                        <option value="author">Author</option>
                        <option value="price">Price</option>
                    </select>
                </label>
            </form>
            <div className="book-container">
                {bookList}
            </div>
        </div>
    )
}

export default withRouter(Books);
