import React, {useState, useEffect} from "react"
import Axios from 'axios';
import { BeatLoader } from "react-spinners";
import '../css/basket.css'

export default function Basket(props){
    const [items, setItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [isBasketEmpty, setisBasketEmpty] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        Axios.get('http://localhost:8000/basket/getItems', {withCredentials: true})
        .then(res => {
            if(res.data === 'emptyBasket'){
                setisBasketEmpty(true);
            }else{
                setisBasketEmpty(false);
                const requestedItems = res.data;
                setItems(requestedItems.array);
                setTotalPrice(requestedItems.totalPrice);
            }
            setLoading(false);
        })
    }, []);

    const clearBasket = () => {
        Axios.get('http://localhost:8000/basket/clear', {withCredentials: true})
        .then(() => {
            setisBasketEmpty(true)
            window.location.reload();
        });
    }

    if(loading){
        return(
            <div className="loading-container">
                <BeatLoader loading/>
            </div>
        )
    }
    if(isBasketEmpty){
        return(
            <div className="basket-empty-container">
                <h1>Your basket is empty. Go shopping.</h1>
            </div>
        )
    }
    const itemList = items.map(book => {
        return(
            <div className="book-in-basket" key={book.item._id}>
                <img src={`/images/${book.item.image}`} alt=""/>
            </div>
        )
    });
    return (
        <div>
            <div className="basket-container">
                {itemList}
            </div>
            Total: ${totalPrice}
            <button onClick={clearBasket}>Clear basket</button>
        </div>
    )
}
