import React, {useState} from 'react';
import {app} from '../../firebase/index';
import Axios from 'axios';

function Add(){
    const [image, setImage] = useState(null);
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [genre, setGenre] = useState('');
    const [price, setPrice] = useState('');
    const [publishedDate, setPublishedDate] = useState('');
    const [description, setDescription] = useState('');
    const [availability, setAvailability] = useState(true);
    const [featured, setFeatured] = useState(false);
    const [alerts, setAlerts] = useState([]);

    const handleChange = event => {
        const img = event.target.files[0];
        setImage(img);
    }

    const uploadImage = () => {
        const storageRef = app.storage().ref();
        const fileRef = storageRef.child(image.name);
        fileRef.put(image).then(() => {
            console.log('File uploaded');
        });
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        Axios.post('http://localhost:8000/admin/add', {title: title, author: author, genre: genre, price: price, 
        publishedDate: publishedDate, description: description, availability: availability, featured: featured, image:image.name}, {withCredentials: true})
        .then((res) => {
            const errors = res.data.errors;
            setAlerts(errors);
        })
        .catch(err =>{
            console.log(err);
        });
    }

    const alertsList = alerts.map(elem => {return <div class="alert" role="alert" key={elem.msg}>{elem.msg}</div>});

    return(
        <div className="form-container">
            <h1>Add a new book</h1>
            <form className="form">
                <input type="text" name="title" placeholder="*Title" value={title} onChange={(e) => setTitle(e.target.value)}/><br />
                <input type="text" name="author" placeholder="*Author" value={author} onChange={(e) => setAuthor(e.target.value)}/><br />
                <input type="text" name="genre" placeholder="*Genre" value={genre} onChange={(e) => setGenre(e.target.value)}/><br />
                <input type="text" name="description" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)}/><br />
                <input type="text" name="price" min="1" max="999" placeholder="*Price" value={price} onChange={(e) => setPrice(e.target.value)}/><br />
                <input type="text" name="publishedDate" placeholder="Published Date" value={publishedDate} onChange={(e) => setPublishedDate(e.target.value)}/><br />
                Is available<input type="checkbox" name="availability" checked={availability} onChange={() => setAvailability(!availability)} /><br />
                Fetured on home page<input type="checkbox" name="featured" checked={featured} onChange={() => setFeatured(!featured)} /><br />
                <input type="file" onChange={(e) => handleChange(e)}/><br />
                <button onClick={(e) => handleSubmit(e)} style={{display: image ? '' : 'none'}}>Upload</button>
                {alertsList}
            </form>      
        </div>
    );
}

export default Add;
