import React, {useState, useEffect} from "react"
import Axios from 'axios';
import { withRouter } from 'react-router-dom';
import {Spinner} from 'react-bootstrap'

import "../css/home.css"

function Home(props){
    const [featuredBooks, setFeaturedBooks] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:8000/books/getAllFeatured', {withCredentials: true})
        .then(res => {
            const requestedBooks = res.data;
            setFeaturedBooks(requestedBooks);
        })
    });

    const featuredBookList = featuredBooks.map(book => {
        return(
            <div className="featured-book-item">
                <div className="featured-image-container">
                    <button onClick={() => {props.history.push("/book/" + book._id)}}>
                        <img src={`/images/${book.image}`} alt=""/>
                    </button>
                </div>
            </div>
        )
    });

    return (
        <div>         
            <div className="nav-bar">
                <button onClick={() => {props.history.push('/books')}}>All</button>
                <button onClick={() => {props.history.push('/books/Novel')}}>Novels</button>
                <button onClick={() => {props.history.push('/books/Autobiographical')}}>Autobiographical</button>
                <button onClick={() => {props.history.push('/books/Sci-Fi')}}>Sci-Fi</button>
            </div>
            <h2 className="featured-title">Featured</h2>
            <div className="featured-container">        
                {featuredBookList}
            </div>       
        </div>
    )
}

export default withRouter(Home);
