import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter} from 'react-router-dom';

function Navigation(props){ 
    const [genres, setGenres] = useState([]);

    useEffect(() => {
        let mounted = true;
        let source = Axios.CancelToken.source();

        Axios.get('http://localhost:8000/genres/getAll', {withCredentials: true, cancelToken: source.token})
        .then(res => {
            const requestedGenres = res.data;
            if(mounted){
                setGenres(requestedGenres);
            }
        });
        return () => {
            mounted = false;
            source.cancel("Cancelling in cleanup");
        }
    });

    const genreList = genres.map(genre => {
        return(
            <button type="button" onClick={() => {props.history.push(`/books?genre=${genre.name}`);  window.location.reload()}} key={genre.name}>{genre.name}</button>
        );
    });

    return(
        <div className="nav-bar">
            <button key="all" type="button" onClick={() => {props.history.push('/books'); window.location.reload()}}>All</button>
            {genreList}
        </div>
    );
}

export default withRouter(Navigation);
