import React, {useState, useEffect} from "react"
import Axios from 'axios';

import "../css/header.css"

export default function Header(props){
    const [isLoggedIn, setIsLoggedIn] = useState(null);

    useEffect(() => {
      Axios.get('http://localhost:8000/checkAuthentication', { withCredentials: true })
      .then(result => {
          // If user is logged in
          setIsLoggedIn(result.data);
      })
    }, []);

    const logOut = () => {
        Axios.get('http://localhost:8000/logout', { withCredentials: true })
        .then(result => {
            props.history.push(result.data);
            setIsLoggedIn(false);
        })
    }
    
    return(
        <div className="header">
            <div>{isLoggedIn ? "Logged In" : "Not logged in"}</div>
            <div><h1>Bookstore</h1></div>
            <div><button style={{display: isLoggedIn ? '' : 'none'}} onClick={logOut}>Log out</button></div>
        </div>
    );
}
