import React, {Component} from "react";
import Axios from 'axios';

class SignUp extends Component{
    constructor(){
        super();
        this.state = {
            email: '',
            password: '',
            alerts: []
        };        
    }

    handleChange = (event) => {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        });
    }

    onSubmit = () => {
        Axios.post('http://localhost:8000/api/signup', {email: this.state.email, password: this.state.password})
        .then((res) => {
            this.setState({alerts: res.data})
        })
        .catch(err =>{
            console.log(err.response.data.errors);
            if(err.response.data.errors !== 'alreadyLoggedIn'){
                this.props.history.push('/signup');
            }
          });
    }

    render(){
        return (
            <div>
                {this.state.alerts.forEach(alert => {
                    return(
                        <div class="alert alert-warning alert-dismissible fade show" role="alert">
                            <strong>Holy guacamole!</strong> You should check in on some of those fields below.
                            <button type="button" class="close" data-dismiss="alert" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                    )
                })}
                <h1>Sign Up</h1>
                <div>
                    <input type="text" name="email" placeholder="Email" onChange={this.handleChange} value={this.state.email}/><br/>
                    <input type="text" name="password" placeholder="Password" onChange={this.handleChange} value={this.state.password}/><br/>
                    <button onClick={this.onSubmit}>Submit</button>
                </div>
                <a href="/">Home</a><br />
                <a href="/login">Log in</a>
            </div>
        );
    }
}

export default SignUp;
